Vue.component("st-wc-color-picker",{template:'\n    <v-row>\n        <v-col cols="12" md="6">\n            Highlight Color\n        </v-col>\n        <v-col cols="12" md="6">\n            <v-color-picker v-model="value" mode="hexa" hide-mode-switch flat @input="update(\'highlightColor\', $event)"></v-color-picker>\n        </v-col>\n    </v-row>\n    ',props:{option:{type:String}},data(){return{value:this.$parent.$parent[this.option]}},methods:{update(option,value){stBus.$emit("update-setting",{option:option,value:value.hex,encode:!1})}}});