Vue.component("st-wc-select",{template:'\n    <v-row v-if="show">\n        <v-col cols="12" md="6" class="text-capitalize">\n            {{ title }}\n        </v-col>\n        <v-col cols="12" md="6">\n            <v-select v-model="value" :items="items" :class="option + \' text-capitalize\'" @change="update(option, $event)" dense :attach="\'.\' + option" item-text="name" item-value="name">\n                <template v-slot:selection="data">\n                    <v-icon>{{ data.item.icon }}</v-icon>\n                    &nbsp;&nbsp;{{ data.item.name }}\n                </template>\n                <template v-slot:item="data">\n                    <v-icon class="mr-5">{{ data.item.icon }}</v-icon>\n                    {{ data.item.name }}\n                </template>\n            </v-select>\n        </v-col>\n    </v-row>\n    ',props:{option:{type:String}},data(){return{parent:this.$parent.$parent,value:this.$parent.$parent[this.option]}},mounted(){stBus.$on("setting-error",({option:option,value:value})=>{this[option]=value})},methods:{is(option){return option===this.option},update(option,value){stBus.$emit("update-setting",{option:option,value:value,encode:!1})}},computed:{items(){return this.parent[this.option+"s"]},title(){return this.option.replace(/([a-z])([A-Z])/g,"$1 $2")},show(){switch(this.option){case"style":return this.parent.highlight;default:return!0}}}});