Vue.component("st-wc-switch",{template:'\n    <v-row>\n        <v-col cols="6" class="text-capitalize pb-0">{{ title }}</v-col>\n        <v-col cols="6" class="d-flex justify-end pb-0">\n            <v-switch v-model="value" dense class="mt-0"\n            @change="update(option, $event)">\n            </v-switch>\n        </v-col>\n    </v-row>\n    ',props:{option:{type:String},title:{type:String}},data(){return{value:this.$parent.$parent[this.option]}},methods:{update(option,value){const setting={option:option,value:value,encode:!1};stBus.$emit("update-setting",setting),stBus.$emit("update-template",setting)}}});