Vue.component("st-wc-alert",{template:'\n    <v-snackbar v-model="show" :color="color" timeout="5000" bottom right>\n        {{ alert.message }}\n        <template v-slot:action="{ attrs }">\n            <v-btn @click="show = false" v-bind="attrs" text icon><v-icon>mdi-close</v-icon></v-btn>\n        </template>\n    </v-snackbar>\n    ',data:()=>({show:!1,alert:{}}),mounted(){stBus.$on("show-alert",alert=>{this.alert=alert,this.show=!0})},beforeDestroy(){stBus.$off("show-alert")},computed:{color(){return this.alert.status?"primary":"error"}}});